import React from "react";
import CodeBlock from "./CodeBlock";

export default function HomeComponent() {
  return (
    <div className="flex flex-row pl-[155px] pr-[155px] bg-blue-400 gap-[200px]  h-[500px]">
      {/* left vala part  */}

      <div className="flex flex-col mt-5">
        {/* <img src="/images/img-2.webp" alt="" height="350px" width="350px"/> */}
        <h1 className="text-3xl font-bold  mt-6 leading-[50px]">
          Welcome to
          <span className="text-yellow-500"> AI Code Reviewer</span> and{" "}
          <span className="text-yellow-400"> Real-Time Code Collaboration</span>
        </h1>

        <p className="text-lg  font-bold mt-5">
          Our AI-powered code reviewer and real-time collaboration tool is
          designed to enhance coding efficiency and teamwork. Whether you're
          working solo or collaborating with a team, our platform provides
          instant feedback, optimizations, and seamless real-time editing.
        </p>
  {/* button */}
        <div className="flex flex-row">
         <a  className="" href="/collbration"> go to collbration</a>

        </div>
      </div>

      {/* right val div */}
      <div className="mt-8">
        <CodeBlock
          code={`<!DOCTYPE html>
                          <html>
                          <head>
                          <link rel="stylesheet" href="styles.css">
                          </head>
                          <body>
                          <h1>This is a heading</h1>
                          <p>This is a paragraph.</p>
                          <p>This is Type Animation </p>
                          </body>
                          </html>`}
          codeColor={"text-yellow-25"}
        ></CodeBlock>
      </div>
    </div>
  );
}
