import React, { useEffect, useRef } from "react";
import "codemirror/lib/codemirror.css";
import "codemirror/mode/javascript/javascript";
import "codemirror/theme/dracula.css";
import "codemirror/addon/edit/closetag";
import "codemirror/addon/edit/closebrackets";
import CodeMirror from "codemirror";

export default function CollaborationEditor() {

  const editorRef = useRef(null);
  const editorInstance = useRef(null); // Store the editor instance

  useEffect(() => {
    if (editorInstance.current) return; // Prevent multiple initializations


    //there may be some error 
  //   if (editorRef.current) {
  //     editorInstance.current = CodeMirror.fromTextArea((editorRef.current = document.getElementById("realTimeEditor"),
  //      {
  //       mode: { name: "javascript", json: true },
  //       theme: "dracula",
  //       autoCloseTags: true,
  //       autoCloseBrackets: true,
  //       lineNumbers: true,
  //     }));

  //     editorInstance.current.setSize("100%", "700px");

  //     editorInstance.on("change",(instance , changes) =>{
  //       console.log(`changes`, instance , changes);
  //     })
  //   }
  // }, []);


  if (editorRef.current) {
    editorInstance.current = CodeMirror.fromTextArea((editorRef.current = document.getElementById("realTimeEditor"),
     {
      mode: { name: "javascript", json: true },
      theme: "dracula",
      autoCloseTags: true,
      autoCloseBrackets: true,
      lineNumbers: true,
    }));

    editorInstance.current.setSize("100%", "700px");

    editorInstance.on("change",(instance , changes) =>{
      console.log(`changes`, instance , changes);
    })
  }
}, []);

  return (
    <div style={{ height: "600px" }}>
      <textarea ref={editorRef} id="realTimeEditor"></textarea>
    </div>
  );
}
