        // <div className="left ">
        //   <div className="code">
        //     <Editor
        //       value={code}
        //       onValueChange={code => setCode(code)}
        //       highlight={code => prism.highlight(code, prism.languages.javascript, "javascript")}
        //       padding={10}
        //       style={{
        //         fontFamily: '"Fira code", "Fira Mono", monospace',
        //         fontSize: 16,
        //         border: "1px solid #ddd",
        //         borderRadius: "5px",
        //         height: "100%",
        //         width: "100%"
        //       }}
        //     />
        //   </div>
        //   <div
        //     onClick={reviewCode}
        //     className="review">Review</div>
        // </div>
    //     <div className="right">
    //       {/* Correct way to use ReactMarkdown inside the component */}
    //       <ReactMarkdown rehypePlugins={[rehypeHighlight]}>
    //         {review}
    //       </ReactMarkdown>
    //     </div>
    //   </main>
    // </div>

    <div className='grid grid-cols-2'>

      <div className=''>
     
          <div className="code">
            <Editor
              value={code}
              onValueChange={code => setCode(code)}
              highlight={code => prism.highlight(code, prism.languages.javascript, "javascript")}
              padding={10}
              style={{
                fontFamily: '"Fira code", "Fira Mono", monospace',
                fontSize: 16,
                border: "1px solid #ddd",
                borderRadius: "5px",
                height: "100%",
                width: "100%"
              }}
            />
        
          <div
            onClick={reviewCode}
            className="review">Review</div>
        </div>

      </div>

      <div className=''>
    
           {/* Correct way to use ReactMarkdown inside the component */}
           <ReactMarkdown rehypePlugins={[rehypeHighlight]}>
             {review}
           </ReactMarkdown>
     
       
      </div>
    </div>
  )
}
