    //         {review}
    //       </ReactMarkdown>
    //     </div>
    //   </main>
    // </div>

   <div className="dark:bg-gray-700">
     <div className="grid grid-cols-2 border-none gap-3  mt-6 ">
      {/* left part code vala  // bg-[#a19c9c] */}
      <div className="bg-gray-400 dark:bg-gray-600">   
        <div className="code">
          <Editor
            value={code}
            onValueChange={(code) => setCode(code)}
            highlight={(code) =>
              prism.highlight(code, prism.languages.javascript, "javascript")
            }
            padding={10}
            style={{
              fontFamily: '"Fira code", "Fira Mono", monospace',
              fontSize: 16,
              border: "1px solid #ddd",
              borderRadius: "5px",
              height: "100%",
              width: "100%",
            }}
          />

          <div
            onClick={reviewCode}
            className="border-none w-[80px] rounded-md flex items-en  bg-primarycolor p-2 font-bold cursor-pointer hover:bg-secondarycolor"
          >
            Review
          </div>
        </div>
      </div>


