import React from 'react'

import {useState} from 'react'
import Client from './Client'
import CollbrationEditor from './CollbrationEditor'


export default function CollbrationComponent() {
  const [clients,setClients] = useState([
    {socketId:1,username:"yogesh"},
    {socketId:2,username:"rana bhagwan"},
    {socketId:1,username:"yogesh rana"},
    {socketId:2,username:"monika"}
  ])
  return (
   <div className='grid grid-cols-12 gap-3'>

    {/* left pannel */}
   <div className='col-span-2 flex flex-col h-screen bg-red-300 rounded-md p-2'>
     <img src="/images/logo.webp" alt="logo"  width="150px" height="50px" className='mx-auto'/>
     <hr className='mt-2 ' />

     {/* list of users */}
     <div className='flex flex-col overflow-auto h-[330px] '>
      {clients.map((client)=>(
        <Client key={client.socketId} username={client.username}/>
      ))}
  

     </div>

     {/* button  */}
     <hr  className='mb-2'/>
     <button className='border-none bg-green-600 p-2 text-white text-bold hover:bg-green-800 mt-2 rounded-lg'>Copy Room ID</button>
     <button className='border-none bg-red-600 p-2 text-white text-bold hover:bg-red-800 mt-2 rounded-lg'>Leave Room</button>


   </div>

    
   {/* right code editor pannel */}
   <div className='col-span-10  flex flex-col h-screen bg-green-200 rounded-md p-2'> </div>
  <CollbrationEditor/>
   </div>
   
  )
}
