import React, { useEffect, useState, useRef } from "react";
import { useParams, useLocation, useNavigate } from "react-router-dom";
import { toast } from "react-hot-toast";
import Client from "./Client";
import CollbrationEditor from "./CollbrationEditor";
import { initSocket } from "../socket";

export default function CollbrationComponent() {
  const socketRef = useRef(null);
  const { roomId } = useParams();
  const location = useLocation();
  const navigate = useNavigate();
  const [clients, setClients] = useState([]);

  const username = location.state?.username;

  console.log("Collaboration Component Loaded. Room ID:", roomId, "Username:", username);

  // ✅ Prevent rendering if state is missing (avoids blank screen issue)
  useEffect(() => {
    if (!username) {
      toast.error("Invalid access! Redirecting...");
      navigate("/");
    }
  }, [username, navigate]);

  useEffect(() => {
    const init = async () => {
      if (!roomId || !username) {
        toast.error("Invalid room ID or username");
        navigate("/");
        return;
      }

      try {
        socketRef.current = await initSocket();

        const handleError = (err) => {
          console.error("Socket error:", err);
          toast.error("Socket connection failed");
          navigate("/");
        };

        socketRef.current.on("connect_error", handleError);
        socketRef.current.on("connect_failed", handleError);

        console.log("Joining room:", roomId, "as:", username);

        socketRef.current.emit("join", { roomId, username });

        socketRef.current.on("joined", ({ clients, username }) => {
          if (username !== location.state?.username) {
            toast.success(`${username} joined the room`);
          }
          setClients(clients);
        });

        return () => {
          socketRef.current.disconnect();
        };
      } catch (error) {
        console.error("Socket initialization failed:", error);
        toast.error("Unable to connect to the server");
        navigate("/");
      }
    };

    init();
  }, [roomId, navigate, username]);

  // ✅ Prevent rendering if redirecting
  if (!username) return null;

  return (
    <div className="grid grid-cols-12 gap-3">
      {/* Left Panel */}
      <div className="col-span-2 flex flex-col h-screen bg-red-300 rounded-md p-2">
        <img src="/images/logo.webp" alt="logo" width="150px" height="50px" className="mx-auto" />
        <hr className="mt-2" />

        {/* List of users */}
        <div className="flex flex-col overflow-auto h-[330px]">
          {clients.map((client) => (
            <Client key={client.socketId} username={client.username} />
          ))}
        </div>

        {/* Buttons */}
        <hr className="mb-2" />
        <button
          className="border-none bg-green-600 p-2 text-white text-bold hover:bg-green-800 mt-2 rounded-lg"
          onClick={() => {
            navigator.clipboard.writeText(roomId);
            toast.success("Room ID copied!");
          }}
        >
          Copy Room ID
        </button>
        <button
          className="border-none bg-red-600 p-2 text-white text-bold hover:bg-red-800 mt-2 rounded-lg"
          onClick={() => navigate("/")}
        >
          Leave Room
        </button>
      </div>

      {/* Right Panel (Code Editor) */}
      <div className="col-span-10 flex flex-col h-screen bg-green-200 rounded-md p-2">
        <CollbrationEditor />
      </div>
    </div>
  );
}








